import '../styles/LoginPage.css';
import React, { useState } from 'react';
import { login } from '../services/user_service';
import { useNavigate } from 'react-router-dom';

function LoginPage({ onLogin }){


    function getUser() {
        try {
          const userAux = document.cookie.split('; ').find(cookie => cookie.startsWith('user='));
          if (userAux) {
            const parsedUser = JSON.parse(decodeURIComponent(userAux.split('=')[1]));
            return parsedUser;
          } else {
            return null;
          }
        } catch (error) {
          console.error("Error parsing user from cookies:", error);
          return null;
        }
      }

    const [username, setUsername] = useState(getUser());
    const [password, setPassword] = useState('');
    const navigate = useNavigate();





    const handleLogin = async () => {
        try {
            const user = await login(username, password);
            if (user) {
                onLogin(user); // Call onLogin with the user object if login succeeds
                if (user.role === 'CLIENT') {
                    navigate('/client'); // Redirect to /client if role is CLIENT
                } else if (user.role === 'ADMIN') {
                    navigate('/admin'); // Redirect to /admin if role is ADMIN
                }
            } else {
                alert('Invalid username or password'); // Show error if login fails
            }
        } catch (error) {
            console.error("Login failed:", error);
            alert("An error occurred during login. Please try again.");
        }
    };



    return(
    <div className='login-container'>
        <h1 className='auth'>Authentication</h1>
        <label className='auth'>Username:</label>
        <input className='auth' placeholder="ex: John123" value={username} onChange={(e) => setUsername(e.target.value)}></input>
        <label className='auth'>Password:</label>
        <input className='auth' placeholder="ex: PassworD!56" value={password} onChange={(e) => setPassword(e.target.value)}></input>
        <button className='auth' type="submit" onClick={handleLogin}>Login</button>
    </div>

    );
}

export default LoginPage;
