import '../styles/Admin.css';
import Device from './device-component/Device';
import User from './user-component/User';
import React, { useState, useEffect } from 'react';
import ModalUser from './user-component/ModalAddUser';
import ModalDevice from './device-component/ModalAddDevice';
import { deleteUser, getUsers } from './../services/user_service';
function Admin() {

    const devices = [
        { description: "Solar Panel", address: "123 Green Lane", energy: "50kW" },
        { description: "Wind Turbine", address: "456 Blue Ridge", energy: "100kW" },
        { description: "Battery Storage", address: "789 Yellow Street", energy: "200kW" }
    ];
    // const users = [
    //     { username: "john_doe", name: "John Doe", email: "john@example.com", password: "password123", role: "Admin" },
    //     { username: "jane_smith", name: "Jane Smith", email: "jane@example.com", password: "securePassword", role: "User" },
    //     { username: "mike_jones", name: "Mike Jones", email: "mike@example.com", password: "mypassword", role: "User" }
    // ];
    const name = 'John';

    const [users, setUsers] = useState([]);
    useEffect(() => {
        const loadUsers = async () => {
            try {
                const fetchedUsers = await getUsers(); // Assume this fetches data from the backend
                setUsers(fetchedUsers);
            } catch (error) {
                console.error("Failed to fetch users:", error);
            }
        };

        loadUsers();
    }, []);


    const [selectedUser, setSelectedUser] = useState(null);
    const handleSelectUser = (username) => {
        setSelectedUser(username);
    };

    const handleDeleteUser = async () => {
        if (selectedUser) {
            try {
                await deleteUser(selectedUser); // Assume deleteUser is an async function
                alert(`User ${selectedUser} has been deleted.`);
                // Optionally, update local state to remove the user from the `users` array
            } catch (error) {
                console.error("Failed to delete user:", error);
            }
        } else {
            alert("Please select a user to delete.");
        }
    };

    const renderDevices = () => {
        return devices.map((device, index) => (
            <Device
                key={index}
                descriptionText={`${device.description}`}
                addressText={`${device.address}`}
                energyText={`${device.energy}`}
            />
        ));
    }
    const renderUsers = () => {
        return users.map((user, index) => (
            <User
                key={index}
                usernameText={`${user.username}`}
                nameText={`${user.name}`}
                emailText={`${user.email}`}
                passwordText={`${user.password}`}
                roleText={`${user.role}`}
                onSelect={handleSelectUser}
            />
        ));
    }

    
    const [isModalOpen, setModalOpen] = useState(false);
    const openModal = () => {
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
    };



    const render = () => {
        if (selectedOption === 'users') {
            return (
                <div>
                    {renderUsers()}
                    <div className='buttonsClass'>
                        <button onClick={openModal}>Add User</button>
                        {isModalOpen && (
                            <ModalUser closeModal={closeModal} />
                        )}
                        <button onClick={() => alert('Edit Device')}>Edit User</button>
                        <button onClick={handleDeleteUser}>Delete User</button>
                    </div>
                </div>
            );



        } else if (selectedOption === 'devices') {
            return (
                <div>
                    {renderDevices()}
                    <div className='buttonsClass'>
                        <button onClick={openModal}>Add Device</button>
                        {isModalOpen && (
                            <ModalDevice closeModal={closeModal} />
                        )}
                        <button onClick={() => alert('Edit Device')}>Edit Device</button>
                        <button onClick={() => alert('Delete Device')}>Delete Device</button>
                    </div>
                </div>
            );
        } else if (selectedOption === 'mapping') {
            return (
                <div>
                    <button onClick={() => alert('Add Device')}>Add Device</button>
                    <button onClick={() => alert('Edit Device')}>Edit Device</button>
                    <button onClick={() => alert('Delete Device')}>Delete Device</button>
                </div>
            );
        }
        return null;
    };








    const [selectedOption, setSelectedOption] = useState('users');
    const handleSwitchChange = (e) => {
        setSelectedOption(e.target.value);
        console.log(e.target.value);
    };









    return (
        <div className='admin-container'>
            <h1>Hi, {name}!</h1>
            <link href="https://cdn.jsdelivr.net/css-toggle-switch/latest/toggle-switch.css" rel="stylesheet" />


            <div className="switch">
                <input id="switch-y" name="tripple" type="radio" value="users" className="switch-input" checked={selectedOption === "users"} onChange={handleSwitchChange} />
                <label for="switch-y" className="switch-label switch-label-y">Users</label>
                <input id="switch-i" name="tripple" type="radio" value="devices" className="switch-input" checked={selectedOption === "devices"} onChange={handleSwitchChange} />
                <label for="switch-i" className="switch-label switch-label-i">Devices</label>
                <input id="switch-n" name="tripple" type="radio" value="mapping" className="switch-input" checked={selectedOption === "mapping"} onChange={handleSwitchChange} />
                <label for="switch-n" className="switch-label switch-label-n">Mapping</label>
                <span className="switch-selector"></span>
            </div>
            {render()}

        </div>


    );
}

export default Admin;
