import '../styles/Modal.css';
import {postUser} from '../services/user_service';
import { useState } from 'react';

function ModalUser({closeModal}) {
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('client'); // Valoare implicită


    async function handleCreateUser() {
        try {
            const result = await postUser("john_doe2", "John Doe", "john2@example.com", "password123", "ADMIN");
    
            if (result.success) {
                console.log("User created successfully!");
            } else {
                alert(`Error: ${result.message}`);
            }
        } catch (error) {
            console.error("Failed to create user:", error);
        }
    }

    return (
        <div className='modal'>
            <span id="close" onClick={closeModal}>&times;</span>
            <div className="modal-content">
            <h2>Add user</h2>
                <label>Username:</label>
                <input placeholder="ex: John123"></input>
                <label>Name:</label>
                <input placeholder="ex: John"></input>
                <label>Email:</label>
                <input placeholder="ex: john@gmail.com"></input>
                <label>Password:</label>
                <input placeholder="ex: PassworD!56"></input>
                <label>Role:</label>
                <select>
                    <option value="client">Client</option>
                    <option value="admin">Admin</option>
                </select>
                <button type='submit' onClick={handleCreateUser}>Add</button>
            </div>
        </div>

    );
}

export default ModalUser;
