import '../styles/Admin.css';
import Device from './Device';
function Admin() {

    const devices = [
        { description: "Solar Panel", address: "123 Green Lane", energy: "50kW" },
        { description: "Wind Turbine", address: "456 Blue Ridge", energy: "100kW" },
        { description: "Battery Storage", address: "789 Yellow Street", energy: "200kW" }
    ];
    const name = 'John';
    const renderDevices = () => {
        return devices.map((device, index) => (
            <Device
                descriptionText={`${device.description}`}
                addressText={`${device.address}`}
                energyText={`${device.energy}`}
            />
        ));
    }

    let selectedOption = "D";
    const handleSwitchChange = (e) => {
        setSelectedOption(e.target.value);
        document.getElementById('admin-root').innerHTML = ''; // Clear the root element
        render(); 
    };



    return (
        <div className='admin-container'>
            <h1>Hi, {name}!</h1>
            <link href="https://cdn.jsdelivr.net/css-toggle-switch/latest/toggle-switch.css" rel="stylesheet" />


            <div class="switch">
                <input id="switch-y" name="tripple" type="radio" value="U" class="switch-input" checked={selectedOption === "U"} />
                <label for="switch-y" class="switch-label switch-label-y">Users</label>
                <input id="switch-i" name="tripple" type="radio" value="D" class="switch-input" checked={selectedOption === "D"} />
                <label for="switch-i" class="switch-label switch-label-i">Devices</label>
                <input id="switch-n" name="tripple" type="radio" value="M" class="switch-input" checked={selectedOption === "M"} />
                <label for="switch-n" class="switch-label switch-label-n">Mapping</label>
                <span class="switch-selector"></span>
            </div>


        </div>


    );
}

export default Admin;
