function setCookie(value){
    document.cookie=`user=${value}`
  }
  
    const handleLogin = (user) => {
      setUser(user);
      setCookie(JSON.stringify(user));
    };
  
  function getUser() {
    try {
      const user = document.cookie.split('; ').find(cookie => cookie.startsWith('user='));
      if (user) {
        const userString = user.split('=')[1];
        const parsedUser = JSON.parse(decodeURIComponent(userString));
        return parsedUser;
      } else {
        return null;
      }
    } catch (error) {
      console.error("Error parsing user from cookies:", error);
      return null;
    }
  }


  export {
   
    getUser,
    handleLogin,
  };
