const API_URL = "http://localhost:8080/user";
const AUTH_URL = "http://localhost:8080/auth";

async function getUserByUsername(username) {
  const response = await fetch(`${API_URL}?username=${username}`,{
    mode: "cors",
    cache: "no-cache",
    credentials: "include",
  });
  const json = await response.json();
  return json;
}

async function deleteComment(username) {
    const response = await fetch(API_URL + "/delete", {
      method: "DELETE",
    //   mode: "cors",
    //   cache: "no-cache",
    //   credentials: "include",
      body: JSON.stringify({
        username: username,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",   //nu cred ca am nevoie de utf
      },
    });

    const data = await response.json();
    return data;
  }
  
  async function updateReportedComment(commentId) {
    return fetch(API_URL + "/comments/report/patch/" + commentId, {
      method: "PATCH",
    });
  }
  


async function postUser(username, name,email,password,role) {
    const response = await fetch(AUTH_URL + "/sign_up", {
      method: "POST",
    //   mode: "cors",
    //   cache: "no-cache",
    //   credentials: "include",
      body: JSON.stringify({
        username: username,
        name: name,
        email: email,
        password: password,
        role:role,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    });
  
    const data = await response.json();
    return data;
  }
