import '../../styles/DisplayUserDevice.css';
function User({ usernameText, nameText, emailText, passwordText, roleText}){
    
    const [isSelected, setIsSelected] = useState(false);

    const handleClick = () => {
        setIsSelected(!isSelected);
    };
    
    return(
    <div className='container' id='user' onClick={() => this.classList.add('.selected')}>
        <label>Username: {usernameText}</label>
        <label>Name: {nameText}</label>
        <label>Email: {emailText}</label>
        <label>Password: {passwordText}</label>
        <label>Role: {roleText}</label>
    </div>
    );
}

export default User;
