import './App.css';
import  {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import React from 'react';
import LoginPage from './components/LoginPage';
import Client from './components/Client';
import Admin from './components/Admin';

function setCookie(value){
  document.cookie=`user=${value}`
}


function getUser() {
  try {
    const user = document.cookie.split('; ').find(cookie => cookie.startsWith('login='));
    if (user) {
      const userString = user.split('=')[1];
      const parsedUser = JSON.parse(decodeURIComponent(userString));
      return parsedUser;
    } else {
      return null;
    }
  } catch (error) {
    console.error("Error parsing user from cookies:", error);
    return null;
  }
}



function App() {

  return (
<Router>
  <Routes>
    <Route path="/" element={<LoginPage />} />
    <Route path="/client" element={<Client />} />
    <Route path="/admin" element={getUser().role === 'ADMIN' ? <Admin /> : <LoginPage onLogin={handleLogin} />} />

  </Routes>
</Router>
  );

}

export default App;






