import '../styles/Client.css';
import Device from './device-component/Device';
function Client(){

    const devices = [
        { description: "Solar Panel", address: "123 Green Lane", maxHourlyConsumption: "50kW" },
        { description: "Wind Turbine", address: "456 Blue Ridge", energy: "100kW" },
        { description: "Battery Storage", address: "789 Yellow Street", energy: "200kW" }
    ];
    const name='John';
    const renderDevices = () => {
        return devices.map((device, index) => (
            <Device 
                descriptionText={`${device.description}`} 
                addressText={`${device.address}`}
                maxHourlyConsumptionText={`${device.maxHourlyConsumption}`}
            />
        ));
    }

    return(
    <div className='client-container'>
        <h1>Energy Management </h1>
        <label id="greeting">Hi, {name}!</label>
        <label>Your devices:</label>
        {renderDevices()}

</div>

    );
}

export default Client;
