import '../../styles/ModalAdd.css';
import { updateUser } from '../../services/user_service';
import { useState, useEffect } from 'react';

function ModalEditUser({ closeModalEdit, usernameText, nameText, emailText, passwordText, roleText }) {
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');

    useEffect(() => {
        setUsername(usernameText);
        setName(nameText);
        setEmail(emailText);
        setPassword(passwordText);
        setRole(roleText);
    }, [usernameText, nameText, emailText, passwordText, roleText]);

    async function handleUpdateUser() {
        try {
            const result = await updateUser(username, name, email, password, role.toUpperCase());
            if (result.message="User updated!") {
                console.log("User edited successfully!");
                closeModalEdit();  // Close modal on success
            } else {
                alert(`Error: ${result.message}`);
            }
        } catch (error) {
            console.error("Failed to edit user:", error);
        }
    }

    return (
        <div className='modal'>
            <span id="close" onClick={closeModalEdit}>&times;</span>
            <div className="modal-content">
                <h2>Edit User</h2>
                <label>Username:</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)} />
                <label>Name:</label>
                <input value={name} onChange={(e) => setName(e.target.value)} />
                <label>Email:</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)} />
                <label>Password:</label>
                <input value={password} onChange={(e) => setPassword(e.target.value)} />
                <label>Role:</label>
                <select value={role} onChange={(e) => setRole(e.target.value)}>
                    <option value="client">Client</option>
                    <option value="admin">Admin</option>
                </select>
                <button type='submit' onClick={handleUpdateUser}>Edit</button>
            </div>
        </div>
    );
}

export default ModalEditUser;
