import '../styles/Admin.css';
import Device from './device-component/Device';
import User from './user-component/User';
import React, { useState, useEffect } from 'react';
import ModalAddUser from './user-component/ModalAddUser';
import ModalAddDevice from './device-component/ModalAddDevice';
import { getUsers } from './../services/user_service';
import { getDevices, deleteDevice } from './../services/device_service';
function Admin() {

    const name = 'John';

    const [selectedDevices, setSelectedDevices] = useState([]); //Aceasta este o listă care stochează ID-urile dispozitivelor selectate. Dacă un dispozitiv este selectat, ID-ul său este adăugat în această listă. Dacă utilizatorul face click din nou pe dispozitiv, ID-ul său va fi eliminat din listă (deci dispozitivul va fi deselectat).
//selectedDevices: pentru a ține evidența dispozitivelor care sunt selectate atunci când utilizatorul face click pe ele.
    const handleSelectDevice = (id) => { //Aceasta funcție este folosită pentru a adăuga sau elimina dispozitivele din lista selectedDevices.
       if (selectedDevices.includes(id)) { // Dacă ID-ul dispozitivului există deja în selectedDevices, înseamnă că dispozitivul este deja selectat, deci îl eliminăm din listă (deselectăm dispozitivul).
            setSelectedDevices(selectedDevices.filter(deviceId => deviceId !== id));
        } else {  // Dacă ID-ul dispozitivului nu există în listă, adăugăm ID-ul în selectedDevices, marcându-l ca fiind selectat.
       
            setSelectedDevices([...selectedDevices, id]);//... ca sa pot selecta mai multe containere
        }
    };

    
    const deleteDev = async () => {
        // Iterăm prin lista dispozitivelor selectate și le ștergem folosind `deleteDevice`
        for (const deviceId of selectedDevices) {
            const device = devices.find(d => d.id === deviceId);
            if (device) {
                try {
                    // Apelează funcția `deleteDevice` pentru a șterge dispozitivul din backend
                    await deleteDevice(device.description, device.address, device.maxHourlyConsumption);
                    console.log(`Deleted device: ${device.description}`);
                } catch (error) {
                    console.error(`Failed to delete device: ${device.description}`, error);
                }
            }
        }
    
        // Filtrează lista de dispozitive locale pentru a elimina cele șterse
        const remainingDevices = devices.filter(device => !selectedDevices.includes(device.id));
        setDevices(remainingDevices);
        setSelectedDevices([]); // Resetează selecțiile după ștergere
    };
    

    

    const [selectedUsers, setSelectedUsers] = useState([]); //Aceasta este o listă care stochează ID-urile dispozitivelor selectate. Dacă un dispozitiv este selectat, ID-ul său este adăugat în această listă. Dacă utilizatorul face click din nou pe dispozitiv, ID-ul său va fi eliminat din listă (deci dispozitivul va fi deselectat).
    //selectedDevices: pentru a ține evidența dispozitivelor care sunt selectate atunci când utilizatorul face click pe ele.
        const handleSelectUser = (id) => { //Aceasta funcție este folosită pentru a adăuga sau elimina dispozitivele din lista selectedDevices.
           if (selectedUsers.includes(id)) { // Dacă ID-ul dispozitivului există deja în selectedDevices, înseamnă că dispozitivul este deja selectat, deci îl eliminăm din listă (deselectăm dispozitivul).
                setSelectedUsers(selectedUssers.filter(userId => userId !== id));
            } else {  // Dacă ID-ul dispozitivului nu există în listă, adăugăm ID-ul în selectedDevices, marcându-l ca fiind selectat.
           
                setSelectedUsers([...selectedUsers, id]);//... ca sa pot selecta mai multe containere
            }
        };
    
        
        const deleteUs = async () => {
            // Iterăm prin lista dispozitivelor selectate și le ștergem folosind `deleteDevice`
            for (const userId of selectedUsers) {
                const user = users.find(d => d.id === userId);
                if (user) {
                    try {
                        // Apelează funcția `deleteDevice` pentru a șterge dispozitivul din backend
                        await deleteUser(user.description, user.address, user.maxHourlyConsumption);
                        console.log(`Deleted user: ${user.description}`);
                    } catch (error) {
                        console.error(`Failed to delete user: ${user.description}`, error);
                    }
                }
            }
        
            // Filtrează lista de dispozitive locale pentru a elimina cele șterse
            const remainingUsers = users.filter(user => !selectedUsers.includes(user.id));
            setUsers(remainingUsers);
            setSelectedUsers([]); // Resetează selecțiile după ștergere
        };



    const [users, setUsers] = useState([]);
    useEffect(() => {
        const loadUsers = async () => {
            try {
                const fetchedUsers = await getUsers();
                setUsers(fetchedUsers);
            } catch (error) {
                console.error("Failed to fetch users:", error);
            }
        };

        loadUsers();
    }, []);



    const [devices, setDevices] = useState([]);
    useEffect(() => {
        const loadDevices = async () => {
            try {
                const fetchedDevices = await getDevices();
                setDevices(fetchedDevices);
            } catch (error) {
                console.error("Failed to fetch devices:", error);
            }
        };

        loadDevices();
    }, []);


    const renderDevices = () => {
        return devices.map((device, index) => (
            <Device
                key={index}
                descriptionText={`${device.description}`}
                addressText={`${device.address}`}
                maxHourlyConsumptionText={`${device.maxHourlyConsumption}`}
                onSelect={handleSelectDevice}
                isSelected={selectedDevices.includes(device.id)} //Verifică dacă ID-ul dispozitivului se află în selectedDevices. Dacă da, atunci dispozitivul este considerat selectat și i se aplică clasa selected.
                id={device.id}
            />
        ));
    }
    const renderUsers = () => {
        return users.map((user, index) => (
            <User
                key={index}
                usernameText={`${user.username}`}
                nameText={`${user.name}`}
                emailText={`${user.email}`}
                passwordText={`${user.password}`}
                roleText={`${user.role}`}
                onSelect={handleSelectDevice}
                isSelected={selectedDevices.includes(device.id)} //Verifică dacă ID-ul dispozitivului se află în selectedDevices. Dacă da, atunci dispozitivul este considerat selectat și i se aplică clasa selected.
                id={device.id}
            />
        ));
    }

    
    const [isModalOpen, setModalOpen] = useState(false);
    const openModal = () => {
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
    };



    const render = () => {
        if (selectedOption === 'users') {
            return (
                <div>
                    {renderUsers()}
                    <div className='buttonsClass'>
                        <button onClick={openModal}>Add User</button>
                        {isModalOpen && (
                            <ModalAddUser closeModal={closeModal} />
                        )}
                        <button onClick={() => alert('Edit User')}>Edit User</button>
                        <button onClick={() => alert('Delete User')}>Delete User</button>
                    </div>
                </div>
            );



        } else if (selectedOption === 'devices') {
            return (
                <div>
                    {renderDevices()}
                    <div className='buttonsClass'>
                        <button onClick={openModal}>Add Device</button>
                        {isModalOpen && (
                            <ModalAddDevice closeModal={closeModal} />
                        )}
                        <button onClick={() => alert('Edit Device')}>Edit Device</button>
                        <button onClick={deleteDev}>Delete Device</button>
                    </div>
                </div>
            );
        } else if (selectedOption === 'mapping') {
            return (
                <div>
                    <button onClick={() => alert('Add Device')}>Add Device</button>
                    <button onClick={() => alert('Edit Device')}>Edit Device</button>
                    <button onClick={() => alert('Delete Device')}>Delete Device</button>
                </div>
            );
        }
        return null;
    };








    const [selectedOption, setSelectedOption] = useState('users');
    const handleSwitchChange = (e) => {
        setSelectedOption(e.target.value);
    };


    return (
        <div className='admin-container'>
            <h1>Hi, {name}!</h1>
            <link href="https://cdn.jsdelivr.net/css-toggle-switch/latest/toggle-switch.css" rel="stylesheet" />


            <div className="switch">
                <input id="switch-y" name="tripple" type="radio" value="users" className="switch-input" checked={selectedOption === "users"} onChange={handleSwitchChange} />
                <label for="switch-y" className="switch-label switch-label-y">Users</label>
                <input id="switch-i" name="tripple" type="radio" value="devices" className="switch-input" checked={selectedOption === "devices"} onChange={handleSwitchChange} />
                <label for="switch-i" className="switch-label switch-label-i">Devices</label>
                <input id="switch-n" name="tripple" type="radio" value="mapping" className="switch-input" checked={selectedOption === "mapping"} onChange={handleSwitchChange} />
                <label for="switch-n" className="switch-label switch-label-n">Mapping</label>
                <span className="switch-selector"></span>
            </div>
            {render()}

        </div>


    );
}

export default Admin;
