import '../styles/Mapping.css';
import { useState, useEffect } from 'react';
import { getUserId } from './../services/user_service';
import { createMapping } from './../services/device_service';
function Mapping() {

    const [username, setUsername] = useState('');
    const [device, setDevice] = useState('');

    async function handleMapping() {
        try {
            const result1 = await updateUser(username, device);
            if (result.success) {
                console.log("User edited successfully!");
            } else {
                alert(`Error: ${result.message}`);
            }
        } catch (error) {
            console.error("Failed to edit user:", error);
        }
    }

    return (
        <div className='mapping-component'>
                <h2>Mapping</h2>
                <label>Select User:</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)} />
                <label>Select Device:</label>
                <input value={device} onChange={(e) => setDevice(e.target.value)} />

                <button type='submit' onClick={handleMapping}>Create</button>
        </div>
    );
}

export default Mapping;
