import './App.css';
import  {BrowserRouter as Router, Routes, Route} from 'react-router-dom';
import React, { useState } from 'react';
import LoginPage from './components/LoginPage';
import Client from './components/Client';
import Admin from './components/Admin';
import { getUser,setCookie } from './services/login_service';


function App() {





function setCookie(value){
    document.cookie=`user=${value}`
  }


  function getUser() {
    try {
      const userAux = document.cookie.split('; ').find(cookie => cookie.startsWith('user='));
      if (userAux) {
        const parsedUser = JSON.parse(decodeURIComponent(userAux.split('=')[1]));
        return parsedUser;
      } else {
        return null;
      }
    } catch (error) {
      console.error("Error parsing user from cookies:", error);
      return null;
    }
  }














  const [user, setUser] = useState(null);

  const handleLogin = (user) => {
    setUser(user);
    setCookie(JSON.stringify(user));
  };




  return (
<Router>
  <Routes>
    <Route path="/" element={<LoginPage onLogin={handleLogin}/>} />
    <Route path="/client" element={getUser().role === 'CLIENT' ? <Client /> : <LoginPage onLogin={handleLogin} />} />
    <Route path="/admin" element={getUser().role === 'ADMIN' ? <Admin /> : <LoginPage onLogin={handleLogin} />} />

  </Routes>
</Router>
  );

}

export default App;






