import '../../styles/ModalAdd.css';
import { updateUser} from '../../services/user_service';
import { useState } from 'react';

function ModalEditUser({closeModalEdit},usernameText,nameText,emailText,passwordText,roleText) {
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('client');


    async function handleUpdateUser() {
        try {
        //    if(verfica daca ai formatele bune){
            const result = await updateUser(username, name, email, password, role.toUpperCase());
    
            if (result.success) {
                console.log("User edited successfully!");
            } else {
                alert(`Error: ${result.message}`);
            }                                           //e pt eroare din back?
        } catch (error) {
            console.error("Failed to edit user:", error);
        }
    }

    return (
        <div className='modal'>
            <span id="close" onClick={closeModalEdit}>&times;</span>
            <div className="modal-content">
            <h2>Add user</h2>
                <label>Username:</label>
                <input value={username} onChange={(e) => setUsername(e.target.value)}>`${usernameText}`</input>
                <label>Name:</label>
                <input value={name} onChange={(e) => setName(e.target.value)}></input>
                <label>Email:</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)}></input>
                <label>Password:</label>
                <input value={password} onChange={(e) => setPassword(e.target.value)} ></input> 
                {/* Proprietatea value={role} este necesară pentru a lega select box-ul de state-ul React și pentru a face controlul input-ului bidirecțional */}
                <label>Role:</label>
                <select value={role} onChange={(e) => setRole(e.target.value)}>
                    <option value="client">Client</option>
                    <option value="admin">Admin</option>
                </select>
                <button type='submit' onClick={handleUpdateUser}>Edit</button>
            </div>
        </div>

    );
}

export default ModalEditUser;
